sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {},
    isDatePast: function(strDate) {
        // rightnow stores the current time
        var rightnow = new GlideDateTime();
        // Create a GlideDateTime object for the When needed date
        var whenNeeded = new GlideDateTime(strDate);

        // If the When needed date is before rightnow, return true. else false
        return rightnow > whenNeeded;
    },
    isToday: function(strDate) {
        var rightnow = new GlideDateTime().getLocalDate();
        // Create a GlideDateTime object for the When needed date
        var whenNeeded = new GlideDateTime(strDate).getLocalDate();

        // if same date, return true. else false
        return rightnow == whenNeeded;
    },
    type: 'NeedItUtils'
};
